"use strict";

var micromatch = require("micromatch");

var IGNORE = ["**/package.json", "**/package-lock.json", "**/node_modules/**", "**/dist/**"];
module.exports = {
  "**/*.{js,json}": function jsJson(files) {
    var match = micromatch.not(files, IGNORE);
    return match.map(function (filename) {
      return "prettier --write '".concat(filename, "'");
    });
  },
  "**/*.md": function md(files) {
    var match = micromatch.not(files, IGNORE);
    return match.map(function (filename) {
      return "prettier --write --parser markdown '".concat(filename, "'");
    });
  }
};