'use strict';

var util = require('util');

var figgyPudding = require('figgy-pudding');

var fixOwner = require('./fix-owner');

var path = require('path');

var rimraf = util.promisify(require('rimraf'));

var uniqueFilename = require('unique-filename');

var _require = require('./disposer'),
    disposer = _require.disposer;

var TmpOpts = figgyPudding({
  tmpPrefix: {}
});
module.exports.mkdir = mktmpdir;

function mktmpdir(cache, opts) {
  opts = TmpOpts(opts);
  var tmpTarget = uniqueFilename(path.join(cache, 'tmp'), opts.tmpPrefix);
  return fixOwner.mkdirfix(cache, tmpTarget).then(function () {
    return tmpTarget;
  });
}

module.exports.withTmp = withTmp;

function withTmp(cache, opts, cb) {
  if (!cb) {
    cb = opts;
    opts = null;
  }

  opts = TmpOpts(opts);
  return disposer(mktmpdir(cache, opts), rimraf, cb);
}

module.exports.fix = fixtmpdir;

function fixtmpdir(cache) {
  return fixOwner(cache, path.join(cache, 'tmp'));
}