'use strict';

var walk = function walk(node, parent, cb) {
  cb(node, parent);
  if (Array.isArray(node.children)) node.children.forEach(function (child) {
    return walk(child, node, cb);
  });
};

var data = {
  name: 'div',
  attribs: {
    id: 'container',
    "class": 'message'
  },
  children: [{
    name: 'strong',
    attribs: {
      "class": 'message'
    },
    children: [{
      text: 'Hello'
    }]
  }, {
    text: ', World!'
  }]
};
walk(data, null, function (node, parent) {
  if (parent) node.parent = parent;
});
module.exports = [data];