/**
 * @fileoverview CheckStyle XML reporter
 * @author Ian Christian Myers
 */
"use strict";

var xmlEscape = require("../xml-escape"); //------------------------------------------------------------------------------
// Helper Functions
//------------------------------------------------------------------------------

/**
 * Returns the severity of warning or error
 * @param {Object} message message object to examine
 * @returns {string} severity level
 * @private
 */


function getMessageType(message) {
  if (message.fatal || message.severity === 2) {
    return "error";
  }

  return "warning";
} //------------------------------------------------------------------------------
// Public Interface
//------------------------------------------------------------------------------


module.exports = function (results) {
  var output = "";
  output += "<?xml version=\"1.0\" encoding=\"utf-8\"?>";
  output += "<checkstyle version=\"4.3\">";
  results.forEach(function (result) {
    var messages = result.messages;
    output += "<file name=\"".concat(xmlEscape(result.filePath), "\">");
    messages.forEach(function (message) {
      output += ["<error line=\"".concat(xmlEscape(message.line), "\""), "column=\"".concat(xmlEscape(message.column), "\""), "severity=\"".concat(xmlEscape(getMessageType(message)), "\""), "message=\"".concat(xmlEscape(message.message)).concat(message.ruleId ? " (".concat(message.ruleId, ")") : "", "\""), "source=\"".concat(message.ruleId ? xmlEscape("eslint.rules.".concat(message.ruleId)) : "", "\" />")].join(" ");
    });
    output += "</file>";
  });
  output += "</checkstyle>";
  return output;
};