/**
 * @fileoverview Compact reporter
 * @author Nicholas C. Zakas
 */
"use strict"; //------------------------------------------------------------------------------
// Helper Functions
//------------------------------------------------------------------------------

/**
 * Returns the severity of warning or error
 * @param {Object} message message object to examine
 * @returns {string} severity level
 * @private
 */

function getMessageType(message) {
  if (message.fatal || message.severity === 2) {
    return "Error";
  }

  return "Warning";
} //------------------------------------------------------------------------------
// Public Interface
//------------------------------------------------------------------------------


module.exports = function (results) {
  var output = "",
      total = 0;
  results.forEach(function (result) {
    var messages = result.messages;
    total += messages.length;
    messages.forEach(function (message) {
      output += "".concat(result.filePath, ": ");
      output += "line ".concat(message.line || 0);
      output += ", col ".concat(message.column || 0);
      output += ", ".concat(getMessageType(message));
      output += " - ".concat(message.message);
      output += message.ruleId ? " (".concat(message.ruleId, ")") : "";
      output += "\n";
    });
  });

  if (total > 0) {
    output += "\n".concat(total, " problem").concat(total !== 1 ? "s" : "");
  }

  return output;
};